import React from "react";
import DATA from "./shop.json";
import { Box, Container, Grid, Heading, Image } from "@chakra-ui/react";
import { NavLink } from "react-router-dom";

export default function Products() {
  console.log(DATA);
  return (
    <Container maxW={"5xl"}>
      <Image src="https://cdn-images.cure.fit/www-curefit-com/image/upload/fl_progressive,f_auto,q_auto:eco,w_1312,ar_1312:560/dpr_2/image/vm/ee3aafaf-b0ac-4a08-8b80-a8f21c893eff.png" />
      <Grid gridTemplateColumns={"repeat(4,1fr)"}>
        {DATA.Tshirts.map((el) => (
          <NavLink to={`products/${el.id}`} key={el.image}>
            <Stack m={"15px"} fontSize="18px" textAlign="left">
              <Image src={el.image} alt="t-shirts" />
              <Text>{el.brand}</Text>
              <Text fontWeight={"bold"}>{el.title}</Text>
              <HStack>
                <Text fontWeight={"bold"}>â‚¹ {el.price}</Text>
                <strike>{el.strike}</strike>
                <Text fontWeight={"bold"} color="pink.400">
                  {el.discount}% off
                </Text>
              </HStack>
            </Stack>
          </NavLink>
        ))}
      </Grid>
    </Container>
  );
}
