import { HStack, Image, Stack, Text, VStack } from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

function getUserById(id) {
  return fetch(`http://localhost:8080/Tshirts/${id}`).then((res) => res.json());
}

export default function SingleProduct() {
  const params = useParams();
  const [el, setData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    getUserById(params.product_id)
      .then((res) => {
        setLoading(true);
        setData(res);
      })
      .catch((err) => {
        setLoading(true);
        console.log(err);
      });
  }, []);

  return (
    <>
      {loading && (
        <HStack justify={"space-evenly"} fontSize="18px" textAlign="left">
          <Image src={el.image} alt="t-shirts" />
          <VStack>
            <Text>{el.brand}</Text>
            <Text fontWeight={"bold"}>{el.title}</Text>
            <HStack>
              <Text fontWeight={"bold"}>â‚¹ {el.price}</Text>
              <strike>{el.strike}</strike>
              <Text fontWeight={"bold"} color="pink.400">
                {el.discount}% off
              </Text>
            </HStack>
          </VStack>
        </HStack>
      )}
    </>
  );
}
