import {
  Badge,
  Box,
  Button,
  Container,
  Flex,
  Grid,
  Heading,
  HStack,
  Image,
  Stack,
  Text,
  VStack,
} from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import { Link, useParams } from "react-router-dom";

function getUserById(id) {
  return fetch(`http://localhost:8080/Tshirts/${id}`).then((res) => res.json());
}

export default function SingleProduct() {
  const params = useParams();
  const [el, setData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getUserById(params.product_id)
      .then((res) => {
        setLoading(false);
        setData(res);
      })
      .catch((err) => {
        setLoading(false);
        console.log(err);
      });
  }, []);

  return (
    <>
      {loading ? (
        console.log(loading)
      ) : (
        <Flex
          direction={{ base: "column", sm: "row" }}
          align={"top"}
          fontSize="18px"
          textAlign="left"
          gap="20px"
          p={"20px"}
        >
          <Grid
            gap="20px"
            w={{ base: "100%", sm: "60%" }}
            gridTemplateColumns={{ base: "repeat(1,1fr)", md: "repeat(2,1fr)" }}
          >
            <Image src={el.image} alt="t-shirts" />
            <Image src={el.image} alt="t-shirts" />
            <Image src={el.image} alt="t-shirts" />
            <Image src={el.image} alt="t-shirts" />
          </Grid>

          <Stack w={{ base: "100%", sm: "40%" }} fontSize="22px" gap={"15px"}>
            <Stack gap={"5px"}>
              <Text>{el.brand}</Text>
              <Heading>{el.title}</Heading>
              <HStack>
                <Text fontWeight={"bold"} color="pink.400">
                  ₹ {el.price}
                </Text>
                <strike>₹ {el.strike}</strike>
                <Badge
                  fontWeight={"bold"}
                  bg="orange"
                  color={"white"}
                  p="1"
                  fontSize="18px"
                >
                  {el.discount}% off
                </Badge>
              </HStack>
              <Text>
                Make Your Move with the Signature Branding Logo Play Tshirt. Add
                the extra groovy vibe your Dance workout wardrobe.
              </Text>
            </Stack>

            <Stack>
              <Text>Choose Size</Text>
              <HStack>
                <Button variant={"outline"}>s</Button>
                <Button variant={"outline"}>m</Button>
                <Button variant={"outline"}>l</Button>
                <Button variant={"outline"}></Button>
                <Button variant={"outline"}></Button>
              </HStack>
            </Stack>
          </Stack>
        </Flex>
      )}
    </>
  );
}
